using Microsoft.Win32;
using SportLeagueOverview.Core.Common;
using SportLeagueOverview.Core.Entitites;
using SportLeagueOverview.Core.Extender;
using SportLeagueOverview.Views;
using SportLeagueOverview.Views.DetailViews;
using System;
using System.Collections.Generic;
using System.ComponentModel;
using System.IO;
using System.Linq;
using System.Runtime.CompilerServices;
using System.Windows;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;

namespace SportLeagueOverview.Core
{
  public class ViewModelBase<T> : INotifyPropertyChanged
  {
    private T m_CurrentItem;
    private List<T> m_CurrentItems;
    private T m_OriginalCurrentItem;
    private bool m_HasChanges;
    private ImageSource m_ImageSource;
    public event EventHandler ImageChanged;
    public event EventHandler CurrentItemChanged;
    private readonly string ImageDefault = "";
    private Window m_NewDialog;
    private bool m_IsNew;

    public ViewModelBase(object ViewModel = null)
    {
      __DoReload();
      __InitializeCommands();
    }

    private void __InitializeCommands()
    {
      Save = new DelegateCommand<object>(Execute_Save);
      Delete = new DelegateCommand<object>(Execute_Delete);
      Reload = new DelegateCommand<object>(Execute_Reload);
      Cancel = new DelegateCommand<object>(Execute_Cancel);
      New = new DelegateCommand<object>(Execute_New);
      SelectImage = new DelegateCommand<object>(Execute_SelectImage);
    }

    #region [Commands]
    public ICommand Save { get; set; }
    public ICommand Delete { get; set; }
    public ICommand Reload { get; set; }
    public ICommand Cancel { get; set; }
    public ICommand SelectImage { get; set; }
    public ICommand New { get; set; }
    #endregion

    public event PropertyChangedEventHandler PropertyChanged;

    public void OnPropertyChanged([CallerMemberName] string PropertyName = null)
    {
      if (PropertyChanged != null)
      {
        var PropertyOldValue = m_OriginalCurrentItem.GetType().GetProperty(PropertyName);
        var OldValue = PropertyOldValue?.GetValue(m_OriginalCurrentItem);
        var PropertyNewValue = CurrentItem.GetType().GetProperty(PropertyName);
        var NewValue = PropertyNewValue?.GetValue(CurrentItem);
        PropertyChanged(this, new PropertyChangedEventArgs(PropertyName));
        if (NewValue == null)
          return;
        if (!NewValue.Equals(OldValue))
        {
          HasChanges = true;
        }
      }
    }

    public void OnCurrentItemChanged()
    {
      foreach (var Property in this.GetType().GetProperties())
      {
        OnPropertyChanged(Property.Name);
      }
    }
    public List<T> CurrentItems
    {
      get
      {
        return m_CurrentItems;
      }
      set
      {
        m_CurrentItems = value;
        OnPropertyChanged(nameof(CurrentItems));
        OnPropertyChanged(nameof(HasItems));
      }
    }

    public T CurrentItem
    {
      get
      {
        if (m_CurrentItem == null)
          m_CurrentItem = Activator.CreateInstance<T>();
        return m_CurrentItem;
      }
      set
      {
        m_CurrentItem = value;
        m_OriginalCurrentItem = (T)(CurrentItem as EntityBase).Clone();
        OnPropertyChanged();
        OnCurrentItemChanged();
        HasChanges = false;
        CurrentItemChanged?.Invoke(CurrentItem, new EventArgs());
      }
    }

    public bool HasChanges
    {
      get
      {
        return m_HasChanges;
      }
      set
      {
        m_HasChanges = value;
        PropertyChanged?.Invoke(this, new PropertyChangedEventArgs(nameof(HasChanges)));
      }
    }

    public bool HasItems => CurrentItems.Any() || IsNew;
    public bool IsNew
    {
      get
      {
        return m_IsNew;
      }
      set
      {
        m_IsNew = value;
        OnPropertyChanged(nameof(IsNew));
      }
    }

    public ImageSource ImageSource
    {
      get
      {
        if (m_ImageSource == null)
          DeserializeImage(ImageDefault);
        return m_ImageSource;
      }
      set
      {
        m_ImageSource = value;
        SerializedImage = __SerializeImage();
        OnPropertyChanged(nameof(ImageSource));
        ImageChanged?.Invoke(this, new EventArgs());
      }
    }

    public byte[] SerializedImage { get; set; }

    public Brush HasChangesBrush => HasChanges ? Brushes.Red : Brushes.Black;

    public void Execute_Save(object sender)
    {
      HasChanges = false;
      EntitySpectator.InvokeSaveRequested(this, new EventArgs());
      DatenbankHelfer.SaveEntity(CurrentItem);
      CurrentItem = Activator.CreateInstance<T>();
      __DoReload();
      EntitySpectator.InvokeSaveCompleted(this, new EventArgs());
    }

    public void Execute_Delete(object sender)
    {
      var Result = DatenbankHelfer.DeleteEntity(CurrentItem);
      if (Result)
        __DoReload();
      EntitySpectator.InvokeDeleteCompleted(this, new EventArgs());
    }

    public void Execute_Reload(object sender)
    {
      __DoReload();
    }

    public void Execute_Cancel(object sender)
    {
      CurrentItem = m_OriginalCurrentItem;
    }

    public void Execute_New(object sender)
    {
      IsNew = true;
      CurrentItem = Activator.CreateInstance<T>();
      CurrentItem.GetType().GetProperty("IsNew").SetValue(CurrentItem, true);
      m_NewDialog = new Window();
      var Type = CurrentItem.GetType();
      if (Type == typeof(AdressEntity))
      {

      }
      else if (Type == typeof(EreignisEntity))
      {

      }
      else if (Type == typeof(MannschaftEntity))
      {
        m_NewDialog.Content = new MannschaftDetailView { DataContext = this};
      }
      else if (Type == typeof(PersonEntity))
      {
        m_NewDialog.Content = new SpielerDetailView { DataContext = this };
      }
      else if (Type == typeof(SpielEntity))
      {
        m_NewDialog.Content = new SpieleDetailView { DataContext = this };
      }
      EntitySpectator.SaveCompleted += (sender, e) =>
      {
        m_NewDialog.Close();
        CurrentItem = CurrentItems.FirstOrDefault();
        OnPropertyChanged(nameof(HasItems));
      };
      m_NewDialog?.ShowDialog();
      //m_NewDialog.Closing => 
    }

    private void __DoReload()
    {
      if (HasChanges)
        return;
      CurrentItems = DatenbankHelfer.ReadEntity<T>();
      if (CurrentItems != null)
        CurrentItem = CurrentItems.FirstOrDefault();
      OnPropertyChanged(nameof(CurrentItems));
    }

    public void Execute_SelectImage(object sender)
    {
      OpenFileDialog OpenDlg = new OpenFileDialog
      {
        Filter = "All files|*.*|Bitmaps|*.bmp|PNG files|*.png|JPEG files|*.jpg|GIF files|*.gif|TIFF files|*.tif|Image files|*.bmp;*.jpg;*.gif;*.png;*.tif"
      };
      if (OpenDlg.ShowDialog() == true)
      {
        var Endings = new List<string> { ".bmp", ".png", ".jpg", ".jpeg", ".gif", ".tif" };
        if (Endings.Any(y => OpenDlg.FileName.EndsWith(y, StringComparison.InvariantCultureIgnoreCase)))
        {
          ImageSource = new BitmapImage(new Uri(OpenDlg.FileName));
        }
      }
    }

    private byte[] __SerializeImage()
    {
      if (ImageSource != null && ((BitmapImage)ImageSource).UriSource?.LocalPath != null)
        return File.ReadAllBytes(((BitmapImage)ImageSource).UriSource.LocalPath);
      return new byte[0];
    }

    public void DeserializeImage(string SourceFromDatabase)
    {
      if (SourceFromDatabase.IsNullOrEmpty())
        SourceFromDatabase = ImageDefault;
      using (MemoryStream ms = new MemoryStream(Convert.FromBase64String(SourceFromDatabase)))
      {
        var imageSource = new BitmapImage();
        imageSource.BeginInit();
        imageSource.CacheOption = BitmapCacheOption.OnLoad;
        imageSource.StreamSource = ms;
        imageSource.EndInit();
        ImageSource = imageSource;
      }

    }
  }

  public static class EntitySpectator
  {
    public static event EventHandler SaveRequested;
    public static event EventHandler SaveCompleted;
    public static event EventHandler DeleteCompleted;

    public static void InvokeSaveRequested(object sender, EventArgs e) => SaveRequested?.Invoke(sender, e);
    public static void InvokeSaveCompleted(object sender, EventArgs e) => SaveCompleted?.Invoke(sender, e);
    public static void InvokeDeleteCompleted(object sender, EventArgs e) => DeleteCompleted?.Invoke(sender, e);
  }
}